mapper.select:
SELECT sys_permission.permission_id,sys_permission.p_id,sys_permission.name,sys_permission.type,sys_permission.url,sys_permission.percode,sys_permission.available,sys_permission.create_time FROM sys_role_permission 
INNER JOIN sys_permission ON sys_role_permission.permission_id = sys_permission.permission_id 
WHERE role_id=#{roleId,jdbcType=INTEGER}
